import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'my-app';
  appHeroName = 'Drogfisher';

  onLike() {
    window.alert(`I like ${this.appHeroName}`);
    this.appHeroName = 'Boothstomper';
  }
}

<app-hero [heroName]="appHeroName" #heroCmp (liked)="onLike()"></app-hero>
<span>{{ heroCmp.heroName }}</span>

<p *ngIf="appHeroName === 'Boothstomper'"> {{ appHeroName }} hero works!</p>
