import { AfterViewInit, Component, ViewChild } from '@angular/core';
import { ProductDetailComponent } from '../product-detail/product-detail.component';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements AfterViewInit {

  @ViewChild(ProductDetailComponent) productDetail:
    ProductDetailComponent | undefined;

  products = ['Webcam', 'Microphone', 'Wireless Keyboard'];

  selectedProduct = '';

  onBuy(name: string) {
    window.alert(`You just bought ${name}!`);
  }

  ngAfterViewInit(): void {
    if (this.productDetail) {
      console.log(`From AfterViewInit ${this.productDetail.name}`);
    }
  }

  trackByProducts(index: number, name: string): string {

  }
}
<h2>Product List</h2>

<ul>
    <li *ngFor="let product of products; trackBy: trackByProducts"
        (click)="selectedProduct = product">
        {{ i + 1}}. {{ product }}
    </li>
</ul>

<app-product-detail *ngIf="selectedProduct; else noProduct"
                    [name]="selectedProduct"
                    (bought)="onBuy($event)"></app-product-detail>
<ng-template #noProduct>
    <p>No product selected!</p>
</ng-template>
